package org.afernandez.springcloud.msvc.usuarios;

import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.cloud.openfeign.EnableFeignClients;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.Base64;

@EnableFeignClients
@SpringBootApplication
public class MsvcUsuariosApplication {

	public static void main(String[] args) throws IOException {
			// Tu cadena Base64 (recortada aquí para el ejemplo)
			String base64Data = "";


		// Decodificar Base64
		byte[] decodedBytes = Base64.getDecoder().decode(base64Data);

		// Verifica que la longitud del archivo decodificado tenga sentido
		if (decodedBytes.length < 1000) {
			System.out.println("El archivo resultante es demasiado pequeño. Revisa el Base64.");
		}

		// Guardar el archivo decodificado como un archivo MP4
		String outputFilePath = "output.mp4";
		Files.write(Paths.get(outputFilePath), decodedBytes);

		System.out.println("Archivo guardado en: " + outputFilePath);
		SpringApplication.run(MsvcUsuariosApplication.class, args);
	}

}
